import React, { Component } from 'react';
import axios from 'axios';
// import { useLocation } from 'react-router-dom';
import queryString from 'query-string'

import './users.css';
import Pagination from '../Pagination/pagination';

class Users extends Component {
	state = {
		users: [],
		error: false,
		currentPageNumber: 1,
		loadPage: false,
	}

	constructor(props) {
		super(props);
		const queryParams = queryString.parse(this.props.location.search)
		if(queryParams.page != 'undefined') {
			this.setState({currentPageNumber: queryParams.page});
		}

	}

	componentDidMount() {
		this.loadUsers();
	}
	componentDidUpdate() {
		this.loadUsers();
	}

	loadUsers() {
		const queryParams = queryString.parse(this.props.location.search)
		// console.log('currentPage'+ this.state.currentPageNumber);
		// console.log(+this.state.currentPageNumber != +queryParams.page);
		// console.log('loadPage: '+this.state.loadPage);
		// console.log('queryPage: '+queryParams.page != 'undefined');
		// if(!this.state.loadPage || (queryParams.page != 'undefined' && +this.state.currentPageNumber != +queryParams.page)) {
		if(!this.state.loadPage) {
		// if(false){
			let page = this.state.currentPageNumber;
			if(queryParams.page) {
				page = queryParams.page;
			}

			axios.get('https://api.github.com/users?since=' + page)
			.then(response => {
				const users = response.data;
				const usersInState = this.state.users;

				users.forEach(user => usersInState.push(user));

				this.setState({users: usersInState, loadPage: true});
				// console.log('currentPage ins'+ this.state.currentPageNumber);
				// console.log('queryPage ins: '+ (queryParams.page != undefined));
				console.log(queryParams.hasOwnProperty('page'));
				console.log('Page: ' + ('page' in queryParams));
				if(queryParams.page != 'undefined') {
					this.setState({currentPageNumber: queryParams.page});
				}
				// console.log('page: '+this.state.currentPageNumber);
			})
			.catch(error => {
				this.setState({error: true});
			});
		}
	}

	render() {
		let users = this.state.users.map(user => {
			return (
				<tr>
					<td>{user.login}</td>
					<td>
						<img src={user.avatar_url} className="avatar" />
					</td>
				</tr>
			)

		});

		return (
			<div className="users">
				<table className='paginationTable' align='center'>
					<tr>
						<td className='searchTd'>
							<input type='text' />
						</td>
						<td className='paginationTd'>
							<Pagination comp='users' currentPageNumber={this.state.currentPageNumber} />
						</td>
					</tr>
				</table>
				<table className='usersTable' align='center'>
					<tr>
						<th>User</th>
						<th>Avatar</th>
					</tr>
					{users}
				</table>
			</div>
		)
	}
}

export default Users;
