import React, { Component } from 'react';
import axios from 'axios';
import { Route, Link, NavLink } from 'react-router-dom';

import './users.css';

class Users extends Component {
	state = {
		users: [],
		error: false,
		currentPageNumber: 1,
	}

	componentDidMount() {
		axios.get('https://api.github.com/users?since=' + this.state.currentPageNumber)
			.then(response => {
				const users = response.data;
				const usersInState = this.state.users;

				users.forEach(user => usersInState.push(user));

				this.setState({users: usersInState});
			})
			.catch(error => {
				this.setState({error: true});
			});
	}
	render() {
		console.log(this.state.users);
		let users = this.state.users.map(user => {
			return (
				<tr>
					<td>{user.login}</td>
					<td>
						<img src={user.avatar_url} className="avatar" />
					</td>
				</tr>
			)

		});

		let pagination = '';
		pagination += (
				<NavLink to={{
					pathname: '/users',
					hash: '#submit',
					search: '?page=' + this.state.currentPageNumber - 1
				}} >Prev</NavLink>
			);
		for(let i=this.state.currentPageNumber; i<=this.state.currentPageNumber + 5; i++) {
			pagination += (<li>
								<NavLink to={{
									pathname: '/users',
									hash: '#submit',
									search: '?page='+{i}
								}} >{i}</NavLink>
							</li>);
		}
		pagination += (
			<NavLink to={{
				pathname: '/users',
				hash: '#submit',
				search: '?page=' + this.state.currentPageNumber + 5
			}} >Next</NavLink>
		);
		return (
			<div className="users">
				<table className='paginationTable' align='center'>
					<tr>
						<td className='searchTd'>
							<input type='text' />
						</td>
						<td className='paginationTd'>
							<nav>
								<ul>

								</ul>
							</nav>

						</td>
					</tr>
				</table>
				<table className='usersTable' align='center'>
					<tr>
						<th>User</th>
						<th>Avatar</th>
					</tr>
					{users}
				</table>
			</div>
		)
	}
}

export default Users;
