import React, { Component } from 'react';
import axios from 'axios';
import queryString from 'query-string'

class UserFollowers extends Component {
	state = {
		followers:[],
		error: false,
		user: null
	}
	componentDidMount() {
		const queryParams = queryString.parse(this.props.location.search)
		console.log(queryParams);
		this.setState({user: queryParams.login});
		axios.get('https://api.github.com/users/?' + queryParams.login)
		.then(response => {
			const dat = response.data;
			let followers = [];
			dat.forEach(follower => {
				return followers = follower.login;
			});
			console.log(followers);
			this.setState({followers: followers});
		})
		.catch(error => {
			this.setState({error: true});
		});
	}

	render() {
		console.log(this.state.error);
		const followers = this.state.followers;
		let followersHtml = followers.map(follower => {
			return (
				<li>{follower}</li>
			)
		});

		return (
			<div className='userFollowers'>
				<h1>List of followers for user: {this.state.user}</h1>
				<ul>
					{followersHtml}
				</ul>
			</div>
		)
	}
}

export default UserFollowers;
