import React, { Component } from 'react';
import axios from 'axios';
// import { useLocation } from 'react-router-dom';
import queryString from 'query-string'

import './users.css';
import Pagination from '../Pagination/pagination';

class Users extends Component {
	state = {
		users: [],
		error: false,
		currentPageNumber: 1,
		nextPagination: null,
	}

	constructor(props) {
		super(props);
		const queryParams = queryString.parse(this.props.location.search)
		if(queryParams.page != 'undefined') {
			this.setState({currentPageNumber: queryParams.page});
		}

	}

	componentDidMount() {
		console.log('test');
		this.loadUsers();
	}

	loadUsers() {
		// if(!this.state.loadedPost || (this.state.loadedPost && this.state.loadedPost.id != this.props.match.params.id)) {
		if(this.state.currentPageNumber) {
			axios.get('https://api.github.com/users?since=' + this.state.currentPageNumber)
			.then(response => {
				const users = response.data;
				const usersInState = this.state.users;

				users.forEach(user => usersInState.push(user));

				this.setState({users: usersInState});
			})
			.catch(error => {
				this.setState({error: true});
			});
		}
	}

	render() {
		let users = this.state.users.map(user => {
			return (
				<tr>
					<td>{user.login}</td>
					<td>
						<img src={user.avatar_url} className="avatar" />
					</td>
				</tr>
			)

		});

		return (
			<div className="users">
				<table className='paginationTable' align='center'>
					<tr>
						<td className='searchTd'>
							<input type='text' />
						</td>
						<td className='paginationTd'>
							<Pagination comp='users' currentPageNumber={this.state.currentPageNumber} />
						</td>
					</tr>
				</table>
				<table className='usersTable' align='center'>
					<tr>
						<th>User</th>
						<th>Avatar</th>
					</tr>
					{users}
				</table>
			</div>
		)
	}
}

export default Users;
